CREATE TABLE `attendance` (  `id` integer PRIMARY KEY AUTOINCREMENT,  `username` text NOT NULL,  `attendance_id` numeric NOT NULL,  `course_id` numeric NOT NULL,  `unit_id` numeric NOT NULL,  `attendance_date` timestamp NOT NULL ,  `team` numeric,  `role` numeric,  `record_score` numeric,  `record_shot` text,  `score` numeric,  `state` numeric,  `long_date` numeric,  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP)