CREATE TABLE `content` (  `id` integer PRIMARY KEY AUTOINCREMENT,  `unit_id` numeric NOT NULL,  `content_id` numeric NOT NULL,  `content` text,  `content_order` numeric,  `quiz_list` text,  `group1` numeric,  `group2` numeric,  `group3` numeric,  `group4` numeric,  `state` numeric,  `long_date` numeric,  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP)