CREATE TABLE `oper` (  `id` integer PRIMARY KEY AUTOINCREMENT,  `oper_id` numeric NOT NULL,  `unit_id` numeric NOT NULL,  `content_id` numeric NOT NULL,  `title` text,  `title_ch` text,  `picture_name` text,  `answer` text,  `team` numeric,  `role` numeric,  `group1` numeric,  `group2` numeric,  `group3` numeric,  `group4` numeric,  `state` numeric,  `long_date` numeric,  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP)