CREATE TABLE `unit` (  `id` integer PRIMARY KEY AUTOINCREMENT,  `course_id` numeric NOT NULL,  `unit_id` numeric NOT NULL,  `unit_name` text,  `unit_schedule` numeric,  `unit_subject` text,  `unit_order` numeric,  `desc_title1` text,  `desc_content1` text,  `desc_title2` text,  `desc_content2` text,  `desc_title3` text,  `desc_content3` text,  `video_url` text,  `video_format` text,  `video_contents` text,  `dfcs_filename` text,  `credit_units` numeric,  `picture_url1` text,  `picture_url2` text,  `picture_url3` text,  `picture_url4` text,  `content_list` text,  `state` numeric,  `long_date` numeric,  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,  `update_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP)